    }
  }

  .offer {
    &__name {
      width: 100%;

      &--popup {
        font-size: 1.3rem;
        margin-top: -1rem;
      }
    }

    &__pledge {
      text-align: left;

      &--popup {
        font-size: 1.3rem;
      }
    }

    &__price--num {
      span {
        font-size: 2;
      }
    }

    &__price--popup {
      font-size: 1.5rem;

      span {
        font-size: 1rem;
      }
    }

    &__num {
