.offer {
  &__radio {
    float: left;
    display: none;

    &--label {
      position: absolute;
      float: left;
      width: 1.7rem;
      height: 1.7rem;
      border-radius: 50%;
      border: 3px solid $color-light-grey;
      margin-top: 0.3rem;
      position: relative;
      cursor: pointer;
    }

    &:checked + &--label::after {
      content: "";
      display: inline-block;
      width: 1rem;
      height: 1rem;
      border-radius: 50%;
      background-color: $color-secondary;
      position: absolute;
      top: 3px;
      left: 3px;
      z-index: 4000;
    }
  }
}

